package com.ting.bean;import java.io.Serializable;/** * Created by liu on 15/8/18. */public class BaseResult implements Serializable {    private boolean status;    private String message;    public String getMessage() {        return message;    }    public void setMessage(String message) {        this.message = message;    }    public boolean isStatus() {        return status;    }    public void setStatus(boolean status) {        this.status = status;    }}