package com.ting.bean;import java.io.Serializable;/** * Created by liu on 15/8/18. */public class BaseResult<T> implements Serializable {    private int status;    private String message;    private T data;    public T getData() {        return data;    }    public void setData(T data) {        this.data = data;    }    public String getMessage() {        return message;    }    public void setMessage(String message) {        this.message = message;    }    public int getStatus() {        return status;    }    public void setStatus(int status) {        this.status = status;    }}